tables.table_schema:
CREATE TABLE EditorialCalendars (
    calendar_id INT PRIMARY KEY,
    calendar_name VARCHAR(50) NOT NULL,
    start_date DATE NOT NULL,
    end_date DATE NOT NULL
);

CREATE TABLE ContentSchedules (
    schedule_id INT PRIMARY KEY,
    schedule_name VARCHAR(50) NOT NULL,
    content_type VARCHAR(20) NOT NULL,
    publish_date DATE NOT NULL
);

CREATE TABLE EditorialCalendar (
    id INT PRIMARY KEY,
    month VARCHAR(20) NOT NULL,
    year INT NOT NULL
);

CREATE TABLE ContentSchedule (
    id INT PRIMARY KEY,
    schedule_date DATE NOT NULL,
    content_title VARCHAR(100) NOT NULL,
    content_type VARCHAR(50) NOT NULL
);

CREATE TABLE PublishingWorkflow (
    id INT PRIMARY KEY,
    workflow_name VARCHAR(50) NOT NULL,
    status VARCHAR(20) NOT NULL
);

CREATE TABLE editorial_calendars ( 
    id INT PRIMARY KEY, 
    calendar_name VARCHAR(255) NOT NULL, 
    start_date DATE NOT NULL, 
    end_date DATE NOT NULL 
);

CREATE TABLE content_schedules ( 
    id INT PRIMARY KEY, 
    content_title VARCHAR(255) NOT NULL, 
    scheduled_date TIMESTAMP, 
    author_name VARCHAR(255) NOT NULL 
);

CREATE TABLE publishing_workflows ( 
    id INT PRIMARY KEY, 
    workflow_name VARCHAR(255) NOT NULL, 
    status VARCHAR(50) NOT NULL, 
    assigned_to VARCHAR(255) 
);

CREATE TABLE users ( 
    id INT PRIMARY KEY, 
    username VARCHAR(100) NOT NULL, 
    password VARCHAR(100) NOT NULL, 
    email VARCHAR(255) NOT NULL 
);

CREATE TABLE PublishingWorkflows (
    workflow_id UUID PRIMARY KEY,
    content_id UUID,
    status TEXT
);

CREATE TABLE Publishers (
    publisher_id UUID PRIMARY KEY,
    name TEXT,
    email TEXT,
    website TEXT
);
