tables.table_schema:
CREATE TABLE episodes (
  episode_id INT PRIMARY KEY,
  title TEXT NOT NULL,
  description TEXT,
  release_date DATE
);

CREATE TABLE listeners (
  listener_id INT PRIMARY KEY,
  name TEXT NOT NULL,
  location TEXT
);

CREATE TABLE listeners (
  listener_id INT PRIMARY KEY,
  listener_name VARCHAR(255),
  email VARCHAR(255),
  subscription_date DATE
);

CREATE TABLE downloads (
  download_id INT PRIMARY KEY,
  episode_id INT,
  listener_id INT,
  download_date DATE
);

CREATE TABLE statistics (
  stats_id INT PRIMARY KEY,
  episode_id INT,
  downloads_count INT,
  listens_count INT
);
