tables.table_schema:
CREATE TABLE ClickThroughRates (
    campaign_id INT,
    date DATE, 
    clicks INT,
    impressions INT
);

CREATE TABLE Conversions (
    conversion_id INT,
    campaign_id INT,
    date DATE, 
    number_of_conversions INT
);

CREATE TABLE click_through_rates ( 
  campaign_id INT, 
  clicks INT, 
  impressions INT, 
  click_through_rate FLOAT 
);

CREATE TABLE conversions ( 
  campaign_id INT, 
  total_conversions INT, 
  conversion_rate FLOAT 
);

CREATE TABLE return_on_investment ( 
  campaign_id INT, 
  investment_amount FLOAT, 
  revenue_generated FLOAT, 
  return_on_investment FLOAT 
);

CREATE TABLE marketing_campaigns ( 
  campaign_id INT PRIMARY KEY, 
  campaign_name VARCHAR(255), 
  start_date DATE, 
  end_date DATE 
);

CREATE TABLE Campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR(255),
    start_date DATE,
    end_date DATE
);

CREATE TABLE Clicks (
    click_id INT PRIMARY KEY,
    campaign_id INT,
    click_timestamp TIMESTAMP,
    user_id INT
);

CREATE TABLE Conversions (
    conversion_id INT PRIMARY KEY,
    campaign_id INT,
    conversion_timestamp TIMESTAMP,
    user_id INT
);

CREATE TABLE ROI (
    roi_id INT PRIMARY KEY,
    campaign_id INT,
    roi_value DECIMAL(10, 2),
    roi_date DATE
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(255),
    email VARCHAR(255)
);
