tables.table_schema:
CREATE TABLE newspapers (
    id UUID PRIMARY KEY,
    name STRING,
    publisher STRING,
    publication_date DATE
);

CREATE TABLE magazines (
    id UUID PRIMARY KEY,
    name STRING,
    publisher STRING,
    publication_date DATE
);

CREATE TABLE newspaper_issues (
  issue_id SERIAL PRIMARY KEY,
  issue_date DATE NOT NULL,
  newspaper_name VARCHAR(255) NOT NULL
);

CREATE TABLE magazine_issues (
  issue_id SERIAL PRIMARY KEY,
  issue_date DATE NOT NULL,
  magazine_name VARCHAR(255) NOT NULL
);

CREATE TABLE article_archive (
  article_id SERIAL PRIMARY KEY,
  article_title VARCHAR(255) NOT NULL,
  publication_date DATE NOT NULL,
  publication_type VARCHAR(50) NOT NULL
);

CREATE TABLE Newspapers (
  newspaper_id INT PRIMARY KEY,
  newspaper_name VARCHAR(50) NOT NULL,
  publication_date DATE NOT NULL
);

CREATE TABLE Magazines (
  magazine_id INT PRIMARY KEY,
  magazine_name VARCHAR(50) NOT NULL,
  publication_date DATE NOT NULL
);

CREATE TABLE NewspaperIssues (
  issue_id INT PRIMARY KEY,
  newspaper_id INT,
  issue_date DATE NOT NULL,
  FOREIGN KEY (newspaper_id) REFERENCES Newspapers(newspaper_id)
);

CREATE TABLE MagazineIssues (
  issue_id INT PRIMARY KEY,
  magazine_id INT,
  issue_date DATE NOT NULL,
  FOREIGN KEY (magazine_id) REFERENCES Magazines(magazine_id)
);
