tables.table_schema:
CREATE TABLE PrintMediaInventory (
  id INT PRIMARY KEY,
  paper_inventory INT,
  ink_inventory INT,
  other_material_inventory INT
);

CREATE TABLE PrintMaterials (
    id INT PRIMARY KEY,
    material_name TEXT,
    quantity INT
);

CREATE TABLE Inventory (
    id INT PRIMARY KEY,
    material_id INT,
    quantity INT,
    FOREIGN KEY (material_id) REFERENCES PrintMaterials(id)
);

CREATE TABLE PrintMediaInventory (
    id INT PRIMARY KEY,
    paper_quantity INT,
    ink_quantity INT,
    other_material_quantity INT
);

CREATE TABLE PaperInventory (
		PaperID UUID PRIMARY KEY,
		PaperName STRING,
		PaperType STRING,
		Quantity INT
);

CREATE TABLE InkInventory (
		InkID UUID PRIMARY KEY,
		InkName STRING,
		InkColor STRING,
		Quantity INT
);

CREATE TABLE OtherMaterialsInventory (
		MaterialID UUID PRIMARY KEY,
		MaterialName STRING,
		MaterialType STRING,
		Quantity INT
);

CREATE TABLE PrintMediaSuppliers (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255) NOT NULL,
    supplier_contact VARCHAR(255) NOT NULL,
    supplier_address VARCHAR(255) NOT NULL
);
