tables.table_schema:
CREATE TABLE articles (
    article_id INT PRIMARY KEY,
    title VARCHAR(255),
    content TEXT,
    source VARCHAR(50)
);

CREATE TABLE sources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR(100)
);

CREATE TABLE article_sources (
    article_id INT,
    source_id INT,
    bias_level VARCHAR(20),
    CONSTRAINT fk_article_id FOREIGN KEY (article_id) REFERENCES articles(article_id),
    CONSTRAINT fk_source_id FOREIGN KEY (source_id) REFERENCES sources(source_id)
);

CREATE TABLE bias_levels (
    level_id INT PRIMARY KEY,
    level_name VARCHAR(50)
);

CREATE TABLE sources (source_id INT PRIMARY KEY,
 source_name VARCHAR,
 domain_URL VARCHAR);
