tables.table_schema:
CREATE TABLE clickbait_articles (
  article_id INT PRIMARY KEY,
  article_title VARCHAR(255) NOT NULL,
  clickbait_flag BOOLEAN
);

CREATE TABLE Article (
    article_id SERIAL PRIMARY KEY,
    title VARCHAR(255) NOT NULL,
    clickbait_flag BOOLEAN
);

CREATE TABLE Review (
    review_id SERIAL PRIMARY KEY,
    article_id INT REFERENCES Article(article_id),
    reviewer_name VARCHAR(100) NOT NULL,
    review_date DATE
);

CREATE TABLE articles (
    article_id INT PRIMARY KEY,
    title VARCHAR,
    is_clickbait BOOLEAN
);

CREATE TABLE clickbait_keywords (
    keyword_id INT PRIMARY KEY,
    keyword VARCHAR
);

CREATE TABLE flagged_articles (
    flag_id INT PRIMARY KEY,
    article_id INT,
    is_reviewed BOOLEAN,
    FOREIGN KEY (article_id) REFERENCES articles(article_id)
);

CREATE TABLE ClickbaitArticles (
  article_id INT PRIMARY KEY,
  article_title VARCHAR(255),
  is_clickbait BOOLEAN
);

CREATE TABLE ReviewTasks (
  task_id INT PRIMARY KEY,
  article_id INT,
  reviewer_id INT,
  task_status VARCHAR(50)
);

CREATE TABLE Reviewers (
  reviewer_id INT PRIMARY KEY,
  reviewer_name VARCHAR(100),
  reviewer_email VARCHAR(255)
);

CREATE TABLE ClickbaitFlags (
  flag_id INT PRIMARY KEY,
  article_id INT,
  flag_reason TEXT
);

CREATE TABLE articles (
    article_id INT PRIMARY KEY,
    title VARCHAR(255),
    content TEXT,
    is_clickbait BOOLEAN
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100)
);

CREATE TABLE flagged_articles (
    flag_id INT PRIMARY KEY,
    article_id INT REFERENCES articles(article_id),
    flagged_by INT REFERENCES users(user_id),
    flag_reason TEXT
);

CREATE TABLE clickbait_keywords (
    keyword_id INT PRIMARY KEY,
    keyword VARCHAR(50)
);

CREATE TABLE article_keywords (
    article_id INT REFERENCES articles(article_id),
    keyword_id INT REFERENCES clickbait_keywords(keyword_id),
    PRIMARY KEY (article_id, keyword_id)
);
