tables.table_schema:
CREATE TABLE sensationalism_scores (
    id SERIAL PRIMARY KEY,
    headline TEXT,
    score FLOAT
);

CREATE TABLE headline_sentiments (
    id SERIAL PRIMARY KEY,
    headline TEXT,
    sentiment TEXT
);

CREATE TABLE sensationalism_headlines (
    headline_id UUID PRIMARY KEY,
    headline_text STRING
);

CREATE TABLE headline_sentiments (
    sentiment_id UUID PRIMARY KEY,
    headline_id UUID REFERENCES sensationalism_headlines(headline_id),
    sentiment_score FLOAT
);

CREATE TABLE headline_keywords (
    keyword_id UUID PRIMARY KEY,
    headline_id UUID REFERENCES sensationalism_headlines(headline_id),
    keyword TEXT
);

CREATE TABLE headlines (
    id INT PRIMARY KEY,
    headline TEXT
);

CREATE TABLE sentiment_analysis (
    id INT PRIMARY KEY,
    headline_id INT,
    sentiment_score FLOAT,
    analyzed_at TIMESTAMP
);

CREATE TABLE keywords (
    id INT PRIMARY KEY,
    keyword TEXT,
    headline_id INT
);

CREATE TABLE entities (
    id INT PRIMARY KEY,
    entity_name TEXT,
    entity_type TEXT,
    headline_id INT
);
