tables.table_schema:
CREATE TABLE user_interactions (
    user_id UUID PRIMARY KEY,
    content_id UUID,
    interaction_type TEXT,
    interaction_date TIMESTAMP
);

CREATE TABLE biased_content (
    content_id UUID PRIMARY KEY,
    content_title TEXT,
    content_category TEXT,
    bias_level INT
);

CREATE TABLE users (user_id INT PRIMARY KEY,
username VARCHAR(50) NOT NULL,
email VARCHAR(100) UNIQUE,
registration_date TIMESTAMP
);

CREATE TABLE content (content_id INT PRIMARY KEY,
content_title VARCHAR(255) NOT NULL,
content_type VARCHAR(50) NOT NULL,
content_rating FLOAT
);

CREATE TABLE interactions (interaction_id INT PRIMARY KEY,
user_id INT,
content_id INT,
interaction_type VARCHAR(20),
interaction_date TIMESTAMP
);

CREATE TABLE recommendations (recommendation_id INT PRIMARY KEY,
user_id INT,
recommended_content_id INT,
recommendation_score FLOAT
);
