CREATE TABLE articles (
  article_id INT NOT NULL PRIMARY KEY,
  title TEXT NOT NULL,
  content TEXT NOT NULL,
  bias_category TEXT NOT NULL
);

CREATE TABLE articles (
    article_id INT PRIMARY KEY,
    title VARCHAR(255),
    content TEXT,
    political_affiliation VARCHAR(50)
);

CREATE TABLE bias_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(50),
    category_description TEXT
);

CREATE TABLE political_parties (
  party_id INT PRIMARY KEY,
  party_name VARCHAR(100),
  ideology VARCHAR(50)
);

CREATE TABLE article_bias (
  article_id INT,
  party_id INT,
  FOREIGN KEY (article_id) REFERENCES articles(article_id),
  FOREIGN KEY (party_id) REFERENCES political_parties(party_id)
);

CREATE TABLE BiasCategories (
    id SERIAL PRIMARY KEY,
    category_name TEXT
);

CREATE TABLE Articles (
    id SERIAL PRIMARY KEY,
    title TEXT,
    content TEXT,
    bias_category_id INT,
    FOREIGN KEY (bias_category_id) REFERENCES BiasCategories(id)
);

CREATE TABLE Users (
    id SERIAL PRIMARY KEY,
    username TEXT,
    email TEXT
);

CREATE TABLE UserPreferences (
    id SERIAL PRIMARY KEY,
    user_id INT,
    bias_category_id INT,
    FOREIGN KEY (user_id) REFERENCES Users(id),
    FOREIGN KEY (bias_category_id) REFERENCES BiasCategories(id)
);

CREATE TABLE political_bias (
 bias_id INT PRIMARY KEY,
 bias_name VARCHAR(50)
);

CREATE TABLE users (
 user_id INT PRIMARY KEY,
 username VARCHAR(50),
 email VARCHAR(100)
);
