tables.table_schema:
CREATE TABLE user_preferences (
    user_id INT PRIMARY KEY,
    genre_preferences TEXT,
    language_preferences TEXT
);

CREATE TABLE recommendations (
    recommendation_id INT PRIMARY KEY,
    user_id INT,
    recommendation_text TEXT,
    FOREIGN KEY (user_id) REFERENCES user_preferences(user_id)
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100)
);

CREATE TABLE products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(50),
    price DECIMAL(10, 2),
    category VARCHAR(50)
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username TEXT NOT NULL,
    email TEXT NOT NULL,
    age INT
);

CREATE TABLE preferences (
    pref_id UUID PRIMARY KEY,
    user_id UUID REFERENCES users(user_id),
    category TEXT,
    preference TEXT
);

CREATE TABLE items (
    item_id UUID PRIMARY KEY,
    item_name TEXT NOT NULL,
    category TEXT
);

CREATE TABLE user_preferences (
    user_id UUID REFERENCES users(user_id),
    pref_id UUID REFERENCES preferences(pref_id),
    score INT
);

CREATE TABLE preferences (
    preference_id INT PRIMARY KEY,
    user_id INT REFERENCES users(user_id),
    category VARCHAR(50),
    value INT
);

CREATE TABLE items (
    item_id INT PRIMARY KEY,
    name VARCHAR(100),
    category VARCHAR(50),
    price DECIMAL(10, 2)
);

CREATE TABLE user_preferences (
    user_id INT REFERENCES users(user_id),
    preference_id INT REFERENCES preferences(preference_id),
    PRIMARY KEY (user_id, preference_id)
);

CREATE TABLE item_preferences (
    item_id INT REFERENCES items(item_id),
    preference_id INT REFERENCES preferences(preference_id),
    PRIMARY KEY (item_id, preference_id)
);
