tables.table_schema:
CREATE TABLE MediaCampaigns (
  campaign_id INT PRIMARY KEY,
  campaign_name VARCHAR(255),
  start_date DATE,
  end_date DATE
);

CREATE TABLE CampaignParticipants (
  participant_id INT PRIMARY KEY,
  participant_name VARCHAR(255),
  campaign_id INT,
  FOREIGN KEY (campaign_id) REFERENCES MediaCampaigns(campaign_id)
);

CREATE TABLE media_literacy_campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name TEXT,
    start_date DATE,
    end_date DATE
);

CREATE TABLE campaign_participants (
    participant_id INT PRIMARY KEY,
    campaign_id INT,
    participant_name TEXT,
    join_date DATE
);
