tables.table_schema:
CREATE TABLE fact_checking_organizations (
  id INT PRIMARY KEY,
  name VARCHAR(255) NOT NULL,
  website VARCHAR(255),
  headquarters VARCHAR(255)
);

CREATE TABLE fact_checking_organization (
    id SERIAL PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    headquarters_location VARCHAR(255),
    website_url VARCHAR(255)
);

CREATE TABLE fact_checking_findings (
    id SERIAL PRIMARY KEY,
    organization_id INT NOT NULL,
    finding_text TEXT,
    rating VARCHAR(50),
    date_published DATE,
    FOREIGN KEY (organization_id) REFERENCES fact_checking_organization(id)
);

CREATE TABLE fact_checking_tags (
    id SERIAL PRIMARY KEY,
    finding_id INT NOT NULL,
    tag_name VARCHAR(50),
    FOREIGN KEY (finding_id) REFERENCES fact_checking_findings(id)
);

CREATE TABLE FactCheckOrganizations (
  id SERIAL PRIMARY KEY,
  name TEXT NOT NULL,
  location TEXT,
  website TEXT
);

CREATE TABLE Findings (
  id SERIAL PRIMARY KEY,
  organization_id INT REFERENCES FactCheckOrganizations(id),
  finding_text TEXT,
  date_found DATE
);

CREATE TABLE Categories (
  id SERIAL PRIMARY KEY,
  category_name TEXT NOT NULL
);

CREATE TABLE OrganizationCategories (
  id SERIAL PRIMARY KEY,
  organization_id INT REFERENCES FactCheckOrganizations(id),
  category_id INT REFERENCES Categories(id)
);

CREATE TABLE fact_checking_organizations (
  organization_id INT PRIMARY KEY,
  organization_name VARCHAR(100) NOT NULL,
  headquarters_location VARCHAR(255) NOT NULL
);

CREATE TABLE fact_checking_findings (
  finding_id INT PRIMARY KEY,
  organization_id INT NOT NULL,
  finding_date DATE NOT NULL,
  finding_text TEXT
);

CREATE TABLE fact_checking_categories (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(50) NOT NULL
);

CREATE TABLE organization_ratings (
  rating_id INT PRIMARY KEY,
  organization_id INT NOT NULL,
  rating_value INT NOT NULL
);

CREATE TABLE finding_tags (
  finding_id INT NOT NULL,
  tag_name VARCHAR(50) NOT NULL
);
