tables.table_schema:
CREATE TABLE Channel (
  channel_id INT PRIMARY KEY,
  channel_name VARCHAR(50) NOT NULL,
  channel_type VARCHAR(50) NOT NULL,
  channel_frequency FLOAT NOT NULL
);

CREATE TABLE channels (
    channel_id INT PRIMARY KEY,
    channel_name VARCHAR(255),
    channel_description TEXT
);

CREATE TABLE presenters (
    presenter_id INT PRIMARY KEY,
    presenter_name VARCHAR(255),
    presenter_bio TEXT,
    presenter_email VARCHAR(255)
);

CREATE TABLE broadcast_programs (
   program_id INT PRIMARY KEY,
   program_name VARCHAR(255) NOT NULL,
   broadcast_time TIMESTAMP NOT NULL,
   program_type ENUM('News', 'Sports', 'Entertainment') NOT NULL
);

CREATE TABLE hosts (
   host_id INT PRIMARY KEY,
   host_name VARCHAR(255) NOT NULL,
   host_type ENUM('Anchor', 'Host', 'Correspondent') NOT NULL
);

CREATE TABLE channels (
   channel_id INT PRIMARY KEY,
   channel_name VARCHAR(255) NOT NULL
);

CREATE TABLE BroadcastChannels (
  channel_id INT PRIMARY KEY,
  channel_name VARCHAR(100),
  channel_type VARCHAR(50),
  channel_owner VARCHAR(100)
);

CREATE TABLE ProgramViewers (
  viewer_id INT PRIMARY KEY,
  viewer_name VARCHAR(100),
  city VARCHAR(100),
  country VARCHAR(100)
);
