tables.table_schema:
CREATE TABLE tv_program_engagement (
    program_id INT PRIMARY KEY,
    program_name VARCHAR(255) NOT NULL,
    audience_size INT NOT NULL,
    engagement_score FLOAT NOT NULL
);

CREATE TABLE tv_programs (
  program_id INT PRIMARY KEY,
  program_name VARCHAR(255),
  genre VARCHAR(50),
  release_date DATE
);

CREATE TABLE TV_Programs (
    program_id SERIAL PRIMARY KEY,
    program_name VARCHAR(255),
    program_genre VARCHAR(100),
    program_duration INT
);

CREATE TABLE Ad_Revenue (
    ad_id SERIAL PRIMARY KEY,
    program_id INT,
    ad_type VARCHAR(50),
    ad_revenue DECIMAL(10, 2)
);

CREATE TABLE tv_programs (
  program_id INT PRIMARY KEY,
  program_name VARCHAR(255),
  air_time TIMESTAMP,
  genre VARCHAR(50)
);
