CREATE TABLE TraditionalMediaDistributionChannels (
  channel_id SERIAL PRIMARY KEY,
  channel_name VARCHAR(255) NOT NULL,
  content_type VARCHAR(50) NOT NULL,
  distribution_platform VARCHAR(100) NOT NULL
);

CREATE TABLE distribution_channels (
    id INT PRIMARY KEY,
    channel_name VARCHAR(255),
    content_type VARCHAR(50),
    subscription_fee DECIMAL(10, 2)
);

CREATE TABLE content_providers (
    id INT PRIMARY KEY,
    provider_name VARCHAR(255),
    contact_info VARCHAR(100),
    contract_start_date DATE
);

CREATE TABLE DistributionChannel (
    channel_id INT PRIMARY KEY,
    channel_name VARCHAR(50) NOT NULL,
    content_type VARCHAR(50) NOT NULL
);

CREATE TABLE Content (
    content_id INT PRIMARY KEY,
    title VARCHAR(100) NOT NULL,
    genre VARCHAR(50),
    is_premium_content BOOLEAN
);

CREATE TABLE distribution_channels (
    id INT PRIMARY KEY,
    channel_name VARCHAR(50),
    channel_type VARCHAR(50),
    created_date TIMESTAMP
);

CREATE TABLE content (
    content_id INT PRIMARY KEY,
    content_name VARCHAR(100),
    content_type VARCHAR(50),
    release_date DATE
);

CREATE TABLE media_company (
    company_id INT PRIMARY KEY,
    company_name VARCHAR(100),
    industry_type VARCHAR(50),
    country VARCHAR(50)
);

CREATE TABLE TraditionalMediaCompanies (
    company_id INT PRIMARY KEY,
    company_name VARCHAR(255) NOT NULL,
    industry VARCHAR(100) NOT NULL
);

CREATE TABLE Content (
    content_id INT PRIMARY KEY,
    content_name VARCHAR(255) NOT NULL,
    category VARCHAR(100) NOT NULL,
    release_date DATE NOT NULL
);

CREATE TABLE DistributionChannels (
    channel_id INT PRIMARY KEY,
    channel_name VARCHAR(255) NOT NULL,
    platform VARCHAR(100) NOT NULL
);

CREATE TABLE ContentDistribution (
    distribution_id INT PRIMARY KEY,
    content_id INT REFERENCES Content(content_id),
    channel_id INT REFERENCES DistributionChannels(channel_id),
    distribution_date DATE NOT NULL
);

CREATE TABLE AccessLogs (
    log_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    content_id INT REFERENCES Content(content_id),
    timestamp TIMESTAMP NOT NULL
);
