tables.table_schema:
CREATE TABLE traditional_media_company (
    company_id INT PRIMARY KEY,
    company_name VARCHAR(255) NOT NULL,
    company_type VARCHAR(50)
);

CREATE TABLE Media_Companies (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    country VARCHAR(255)
);

CREATE TABLE Ratings (
    id INT PRIMARY KEY,
    company_id INT,
    rating FLOAT,
    date DATE
);

CREATE TABLE Shows (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    genre VARCHAR(255),
    duration INT
);

CREATE TABLE Analysts (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    department VARCHAR(255)
);
