tables.table_schema:
CREATE TABLE new_media_content_recommendations (
    id UUID PRIMARY KEY,
    media_title STRING,
    genre STRING,
    rating FLOAT
);

CREATE TABLE user_preferences (
    user_id UUID PRIMARY KEY,
    favorite_genres ARRAY<STRING>,
    last_watched_media_id UUID,
    last_watched_timestamp TIMESTAMP
);

CREATE TABLE recommendation_algorithm (
  user_id INT PRIMARY KEY,
  recommendation_id INT,
  media_title VARCHAR
);

CREATE TABLE user_profile (
  user_id INT PRIMARY KEY,
  name VARCHAR,
  age INT,
  gender VARCHAR
);

CREATE TABLE media_content (
  media_id INT PRIMARY KEY,
  media_title VARCHAR,
  media_type VARCHAR
);

CREATE TABLE users (
id INT PRIMARY KEY,
username VARCHAR(50) NOT NULL,
email VARCHAR(100) NOT NULL,
subscription_type VARCHAR(20) NOT NULL
);

CREATE TABLE Recommendations (
    recommendation_id UUID PRIMARY KEY,
    recommendation_title TEXT,
    media_id UUID
);

CREATE TABLE Users (
    user_id UUID PRIMARY KEY,
    user_name TEXT,
    user_email TEXT
);

CREATE TABLE Media (
    media_id UUID PRIMARY KEY,
    media_type TEXT,
    media_genre TEXT
);

CREATE TABLE Views (
    view_id UUID PRIMARY KEY,
    user_id UUID,
    media_id UUID,
    view_timestamp TIMESTAMP
);

CREATE TABLE Ratings (
    rating_id UUID PRIMARY KEY,
    user_id UUID,
    media_id UUID,
    rating_value INT
);
