tables.table_schema:
CREATE TABLE EventCreators (
    creator_id INT PRIMARY KEY,
    creator_name VARCHAR(255) NOT NULL,
    email VARCHAR(255) UNIQUE NOT NULL
);

CREATE TABLE Guests (
    guest_id INT PRIMARY KEY,
    guest_name VARCHAR(255) NOT NULL,
    email VARCHAR(255) UNIQUE NOT NULL
);

CREATE TABLE event_planners (
    event_planner_id INT PRIMARY KEY,
    event_planner_name VARCHAR(50) NOT NULL,
    event_planner_email VARCHAR(100) UNIQUE NOT NULL
);

CREATE TABLE events (
    event_id INT PRIMARY KEY,
    event_name VARCHAR(50) NOT NULL,
    event_date DATE NOT NULL,
    event_location VARCHAR(100) NOT NULL
);

CREATE TABLE guests (
    guest_id INT PRIMARY KEY,
    guest_name VARCHAR(50) NOT NULL,
    guest_email VARCHAR(100) UNIQUE NOT NULL
);
