tables.table_schema:
CREATE TABLE messages (
    message_id INT PRIMARY KEY,
    user_id INT,
    message_text TEXT,
    message_timestamp TIMESTAMP
);

CREATE TABLE message_alerts (
    id INT PRIMARY KEY,
    message_id INT,
    user_id INT,
    created_at TIMESTAMP
);

CREATE TABLE updates (
    update_id SERIAL PRIMARY KEY,
    user_id INT,
    update_text TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE updates (
    update_id INT PRIMARY KEY,
    update_content TEXT,
    user_id INT,
    timestamp TIMESTAMP
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(255),
    join_date DATE
);
