CREATE TABLE media_outlets (
    id UUID PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    type VARCHAR(50) NOT NULL,
    audience_reach INT
);

CREATE TABLE media_outlets (
    outlet_id INT PRIMARY KEY,
    outlet_name VARCHAR(255),
    audience_reach INT
);

CREATE TABLE audience_reach (
    audience_id INT PRIMARY KEY,
    outlet_id INT,
    audience_count INT,
    FOREIGN KEY (outlet_id) REFERENCES media_outlets(outlet_id)
);

CREATE TABLE media_outlets (
  outlet_id INT PRIMARY KEY,
  outlet_name VARCHAR(255),
  outlet_type VARCHAR(50),
  audience_reach INT
);

CREATE TABLE outlet_audience_mapping (
  mapping_id INT PRIMARY KEY,
  outlet_id INT,
  audience_id INT,
  mapping_date DATE
);

CREATE TABLE MediaOutlets (
    outlet_id SERIAL PRIMARY KEY,
    outlet_name VARCHAR(255) NOT NULL,
    outlet_type VARCHAR(50) NOT NULL,
    audience_reach INT
);

CREATE TABLE Authors (
    author_id SERIAL PRIMARY KEY,
    author_name VARCHAR(255) NOT NULL,
    author_email VARCHAR(255) UNIQUE
);

CREATE TABLE Readers (
    reader_id SERIAL PRIMARY KEY,
    reader_name VARCHAR(255) NOT NULL,
    reader_email VARCHAR(255) UNIQUE
);
