tables.table_schema:
CREATE TABLE MediaConsumption (
    user_id INT PRIMARY KEY,
    media_type STRING,
    time_stamp TIMESTAMP,
    duration_minutes INT
);

CREATE TABLE MediaConsumptionTrends (
    user_id INT PRIMARY KEY,
    media_type VARCHAR(255),
    time_stamp TIMESTAMP,
    hours_consumed FLOAT
);

CREATE TABLE UserDemographics (
    user_id INT PRIMARY KEY,
    age INT,
    gender VARCHAR(10),
    location VARCHAR(255)
);

CREATE TABLE MediaConsumptionHistory (
    user_id INT NOT NULL,
    media_type VARCHAR(50) NOT NULL,
    hours_watched DECIMAL NOT NULL,
    date_watched DATE NOT NULL
);

CREATE TABLE UserDemographics (
    user_id INT NOT NULL,
    age INT NOT NULL,
    gender VARCHAR(10) NOT NULL,
    location VARCHAR(100) NOT NULL
);

CREATE TABLE MediaTrends (
    trend_id INT NOT NULL,
    media_type VARCHAR(50) NOT NULL,
    trend_description TEXT NOT NULL,
    trend_date DATE NOT NULL
);

CREATE TABLE media_consumption_trends (
    id INT PRIMARY KEY,
    media_type VARCHAR(255),
    consumption_hours FLOAT,
    date DATE
);

CREATE TABLE user_demographics (
    user_id INT PRIMARY KEY,
    age INT,
    gender VARCHAR(10),
    location VARCHAR(100)
);

CREATE TABLE media_platforms (
    platform_id INT PRIMARY KEY,
    platform_name VARCHAR(100),
    launch_date DATE
);

CREATE TABLE media_content (
    content_id INT PRIMARY KEY,
    title VARCHAR(255),
    genre VARCHAR(50),
    release_date DATE
);
