tables.table_schema:
CREATE TABLE users (
  user_id INT PRIMARY KEY,
  age INT,
  gender VARCHAR(10),
  location VARCHAR(50)
);

CREATE TABLE media_preferences (
  user_id INT,
  genre VARCHAR(20),
  rating FLOAT,
  FOREIGN KEY (user_id) REFERENCES users(user_id)
);

CREATE TABLE UserDemographics (
    user_id UUID PRIMARY KEY,
    age INT,
    gender VARCHAR(10),
    location VARCHAR(50)
);

CREATE TABLE UserPreferences (
    user_id UUID PRIMARY KEY,
    genre_preference VARCHAR(50),
    language_preference VARCHAR(50)
);

CREATE TABLE users (user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    age INT,
    gender VARCHAR(10),
    location VARCHAR(100) NOT NULL
);

CREATE TABLE media (media_id INT PRIMARY KEY,
    media_title VARCHAR(100) NOT NULL,
    media_type VARCHAR(50),
    release_date DATE,
    genre VARCHAR(50)
);

CREATE TABLE user_preferences (user_id INT,
    preference_id INT,
    rating DECIMAL(2,1),
    PRIMARY KEY (user_id, preference_id),
    FOREIGN KEY (user_id) REFERENCES users(user_id)
);
