tables.table_schema:
CREATE TABLE media_regulatory_authorities ( 
 media_outlet_id INT PRIMARY KEY, 
 outlet_name VARCHAR(255), 
 censorship_status BOOLEAN 
);

CREATE TABLE MediaOutlets (
    outlet_id INT PRIMARY KEY,
    name VARCHAR(255),
    country VARCHAR(50),
    compliance_score DECIMAL
);

CREATE TABLE CensorshipRules (
    rule_id INT PRIMARY KEY,
    description TEXT,
    keywords VARCHAR(255),
    severity INT
);
