tables.table_schema:
CREATE TABLE Complaints (
    complaint_id INT PRIMARY KEY,
    media_type VARCHAR(50),
    complaint_text TEXT,
    complaint_date DATE
);

CREATE TABLE Media (
    media_id INT PRIMARY KEY,
    media_name VARCHAR(50),
    media_type VARCHAR(50),
    media_regulator VARCHAR(50)
);

CREATE TABLE Complainants (
    complainant_id INT PRIMARY KEY,
    complainant_name VARCHAR(50),
    complainant_email VARCHAR(50),
    complainant_phone VARCHAR(15)
);

CREATE TABLE Responses (
    response_id INT PRIMARY KEY,
    complaint_id INT,
    response_text TEXT,
    response_date DATE
);
