tables.table_schema:
CREATE TABLE advertising_expenditures (
    company_name VARCHAR(255),
    media_type VARCHAR(50),
    expenditure_amount DECIMAL(10, 2)
);

CREATE TABLE advertising_expenditures (
    id INT PRIMARY KEY,
    media_company VARCHAR(255),
    expenditure_amount DECIMAL(10, 2),
    timestamp TIMESTAMP
);

CREATE TABLE advertising_expenditures (
    company_name VARCHAR(255),
    media_platform VARCHAR(255),
    expenditure_amount DECIMAL(10, 2)
);

CREATE TABLE advertising_companies (
    company_id INT,
    company_name VARCHAR(255),
    industry VARCHAR(255)
);

CREATE TABLE regulatory_agencies (
    agency_id INT,
    agency_name VARCHAR(255),
    jurisdiction VARCHAR(255)
);

CREATE TABLE media_company (
    company_id INT PRIMARY KEY,
    company_name VARCHAR(255) NOT NULL,
    company_location VARCHAR(255) NOT NULL
);

CREATE TABLE ad_agency (
    agency_id INT PRIMARY KEY,
    agency_name VARCHAR(255) NOT NULL
);
