tables.table_schema:
CREATE TABLE Media_Ownership (
    id INT,
    company_name VARCHAR,
    owner VARCHAR,
    industry VARCHAR
);

CREATE TABLE Media_Companies (
    company_id INT PRIMARY KEY,
    company_name TEXT
);

CREATE TABLE Media_Mergers (
    merger_id INT PRIMARY KEY,
    merger_date DATE,
    acquiring_company_id INT,
    acquired_company_id INT
);

CREATE TABLE Market_Competition (
    competition_id INT PRIMARY KEY,
    competition_name TEXT
);

CREATE TABLE Competition_Analysis (
    analysis_id INT PRIMARY KEY,
    merger_id INT,
    competition_id INT,
    market_share DECIMAL
);

CREATE TABLE media_companies (
    company_id INT PRIMARY KEY,
    company_name VARCHAR(50) NOT NULL,
    industry VARCHAR(50) NOT NULL,
    revenue DECIMAL(15, 2)
);

CREATE TABLE market_competition (
    competition_id INT PRIMARY KEY,
    merger_id INT NOT NULL,
    market_share FLOAT,
    competition_level VARCHAR(50) NOT NULL
);

CREATE TABLE regulatory_agencies (
    agency_id INT PRIMARY KEY,
    agency_name VARCHAR(50) NOT NULL,
    jurisdiction VARCHAR(50)
);

CREATE TABLE merger_impact (
    impact_id INT PRIMARY KEY,
    merger_id INT NOT NULL,
    impact_description TEXT
);
