tables.table_schema:
CREATE TABLE media_organizations (
    media_id UUID PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL
);

CREATE TABLE audit_logs (
    log_id UUID PRIMARY KEY,
    media_id UUID REFERENCES media_organizations(media_id),
    audit_date TIMESTAMP NOT NULL,
    compliance_status BOOLEAN NOT NULL
);

CREATE TABLE media_organizations (
    organization_id INT PRIMARY KEY,
    organization_name TEXT,
    location TEXT
);

CREATE TABLE regulatory_bodies (
    body_id INT PRIMARY KEY,
    body_name TEXT,
    location TEXT
);

CREATE TABLE regulatory_bodies (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL
);

CREATE TABLE auditors (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    role VARCHAR(255) NOT NULL
);

CREATE TABLE MediaOrganization (
    org_id INT PRIMARY KEY,
    org_name VARCHAR(255),
    registration_date DATE,
    compliance_status BOOLEAN
);

CREATE TABLE RegulatoryStandards (
    standard_id INT PRIMARY KEY,
    standard_name VARCHAR(255),
    description TEXT
);
