tables.table_schema:
CREATE TABLE restaurant_reviews (
    review_id INT PRIMARY KEY,
    restaurant_id INT,
    user_id INT,
    city VARCHAR(255),
    rating FLOAT
);

CREATE TABLE restaurants (
    restaurant_id INT PRIMARY KEY,
    restaurant_name VARCHAR(255),
    city VARCHAR(255),
    cuisine_type VARCHAR(255)
);

CREATE TABLE Restaurant (id UUID PRIMARY KEY,
name VARCHAR(255),
city VARCHAR(100));

CREATE TABLE User (id UUID PRIMARY KEY,
username VARCHAR(50),
email VARCHAR(100));
