tables.table_schema:
CREATE TABLE food_pairings (
  food_id INT PRIMARY KEY,
  food_name VARCHAR(255),
  wine_id INT,
  pairing_description TEXT
);

CREATE TABLE wine_pairings (
  wine_id INT PRIMARY KEY,
  wine_name VARCHAR(255),
  food_id INT,
  pairing_rating INT
);

CREATE TABLE Food (
  food_id INT PRIMARY KEY,
  food_name VARCHAR(255) NOT NULL,
  cuisine_type VARCHAR(100) NOT NULL
);

CREATE TABLE Wine (
  wine_id INT PRIMARY KEY,
  wine_name VARCHAR(255) NOT NULL,
  region VARCHAR(100) NOT NULL
);

CREATE TABLE Pairings (
  id INT PRIMARY KEY,
  food_id INT NOT NULL,
  wine_id INT NOT NULL
);

CREATE TABLE Food (food_id INT PRIMARY KEY, food_name VARCHAR(50), food_type VARCHAR(50));

CREATE TABLE Wine (wine_id INT PRIMARY KEY, wine_name VARCHAR(50), wine_type VARCHAR(50));

CREATE TABLE Pairing (pairing_id INT PRIMARY KEY, food_id INT, wine_id INT);

CREATE TABLE FoodCategory (category_id INT PRIMARY KEY, category_name VARCHAR(50));

CREATE TABLE WineCategory (category_id INT PRIMARY KEY, category_name VARCHAR(50));
