tables.table_schema:
CREATE TABLE visas (
    visa_id INT PRIMARY KEY,
    country_name VARCHAR(255),
    visa_type VARCHAR(255),
    valid_until DATE
);

CREATE TABLE passports (
    passport_id INT PRIMARY KEY,
    passport_number VARCHAR(50),
    issue_date DATE,
    expiry_date DATE
);

CREATE TABLE passport_info (
    id SERIAL PRIMARY KEY,
    passport_number VARCHAR(20) NOT NULL,
    issue_date DATE,
    expiration_date DATE
);

CREATE TABLE country_information (
    id SERIAL PRIMARY KEY,
    country_name VARCHAR(50) NOT NULL,
    capital_city VARCHAR(50),
    currency VARCHAR(10)
);

CREATE TABLE passport_details (
    passport_id SERIAL PRIMARY KEY,
    passport_number VARCHAR(20) NOT NULL,
    issue_date DATE,
    expiration_date DATE
);

CREATE TABLE visa_information (
    visa_id UUID PRIMARY KEY,
    country_name VARCHAR(255),
    visa_type VARCHAR(50),
    visa_expiry DATE
);

CREATE TABLE passport_information (
    passport_id UUID PRIMARY KEY,
    passport_number VARCHAR(20),
    issuing_country VARCHAR(100),
    expiration_date DATE
);

CREATE TABLE country_requirements (
    country_id UUID PRIMARY KEY,
    country_name VARCHAR(255),
    visa_required BOOLEAN,
    passport_required BOOLEAN
);
