tables.table_schema:
CREATE TABLE digital_nomads (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    email VARCHAR(255),
    location VARCHAR(255)
);

CREATE TABLE remote_workers (
    id INT PRIMARY KEY,
    occupation VARCHAR(255),
    skills VARCHAR(255),
    experience_years INT
);

CREATE TABLE networking_contacts (
    id INT PRIMARY KEY,
    contact_name VARCHAR(255),
    email VARCHAR(255),
    linkedin_profile VARCHAR(255)
);

CREATE TABLE work_preferences (
    id INT PRIMARY KEY,
    job_type VARCHAR(255),
    preferred_location VARCHAR(255),
    availability VARCHAR(255)
);
