tables.table_schema:
CREATE TABLE SIM_Cards (
    sim_id INT PRIMARY KEY,
    phone_number VARCHAR(20),
    country_code VARCHAR(5),
    data_plan_id INT
);

CREATE TABLE Data_Plans (
    data_plan_id INT PRIMARY KEY,
    plan_name VARCHAR(50),
    data_limit INT,
    price DECIMAL(10, 2)
);

CREATE TABLE sim_cards (
  id INT PRIMARY KEY,
  phone_number VARCHAR(10) NOT NULL,
  traveler_name VARCHAR(50) NOT NULL,
  country_code CHAR(2) NOT NULL
);

CREATE TABLE data_plans (
  plan_id INT PRIMARY KEY,
  plan_name VARCHAR(50) NOT NULL,
  data_limit INT NOT NULL,
  price DECIMAL(8, 2) NOT NULL
);

CREATE TABLE sim_usage (
  usage_id INT PRIMARY KEY,
  sim_id INT NOT NULL,
  data_usage INT NOT NULL,
  date_used TIMESTAMP NOT NULL
);

CREATE TABLE plan_usage (
  usage_id INT PRIMARY KEY,
  plan_id INT NOT NULL,
  data_used INT NOT NULL,
  date_used TIMESTAMP NOT NULL
);

CREATE TABLE sim_cards (
    sim_id UUID PRIMARY KEY,
    phone_number TEXT,
    country_code TEXT,
    data_balance INT
);

CREATE TABLE data_plans (
    plan_id UUID PRIMARY KEY,
    plan_name TEXT,
    data_quota INT,
    price DECIMAL
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username TEXT,
    email TEXT UNIQUE,
    phone_number TEXT UNIQUE
);
