tables.table_schema:
CREATE TABLE language_resources (
    id SERIAL PRIMARY KEY,
    resource_name VARCHAR(100) NOT NULL,
    resource_type VARCHAR(50),
    language VARCHAR(50) NOT NULL
);

CREATE TABLE UserProgress (
    user_id INT,
    language VARCHAR(50),
    progress INT,
    FOREIGN KEY (user_id) REFERENCES Users(id)
);

CREATE TABLE language_resources(
  resource_id INT PRIMARY KEY,
  resource_name VARCHAR(255),
  resource_type VARCHAR(50),
  resource_content TEXT
);

CREATE TABLE study_materials(
  material_id INT PRIMARY KEY,
  material_name VARCHAR(255),
  material_type VARCHAR(50),
  material_content TEXT
);

CREATE TABLE languages(
  language_id INT PRIMARY KEY,
  language_name VARCHAR(50),
  proficiency_level VARCHAR(20)
);

CREATE table LanguageResources (
    resource_id INT PRIMARY KEY,
    resource_name VARCHAR(255),
    resource_type VARCHAR(50),
    resource_url VARCHAR(255),
    date_added DATE
);

CREATE table StudyMaterials (
    material_id INT PRIMARY KEY,
    material_name VARCHAR(255),
    material_type VARCHAR(50),
    material_url VARCHAR(255),
    date_added DATE
);

CREATE table LanguageCategories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(100),
    description TEXT
);

CREATE table Users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    country VARCHAR(50),
    date_joined DATE
);

CREATE table UserResources (
    user_id INT,
    resource_id INT,
    PRIMARY KEY (user_id, resource_id),
    FOREIGN KEY (user_id) REFERENCES Users(user_id),
    FOREIGN KEY (resource_id) REFERENCES LanguageResources(resource_id)
);
