tables.table_schema:
CREATE TABLE Travelers (
    traveler_id INT PRIMARY KEY,
    traveler_name VARCHAR(50),
    traveler_location VARCHAR(100)
);

CREATE TABLE photos (
    photo_id INT PRIMARY KEY,
    photo_title TEXT,
    location TEXT,
    date_taken DATE
);

CREATE TABLE videos (
    video_id INT PRIMARY KEY,
    video_title TEXT,
    location TEXT,
    date_recorded DATE
);

CREATE TABLE tags (
    tag_id INT PRIMARY KEY,
    tag_name TEXT
);
