tables.table_schema:
CREATE TABLE travel_itineraries (
    id INT PRIMARY KEY,
    itinerary_name VARCHAR(255) NOT NULL,
    cultural_site VARCHAR(255) NOT NULL
);

CREATE TABLE cultural_sites (
    id INT PRIMARY KEY,
    site_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL
);

CREATE TABLE cultural_sites (
    site_id UUID PRIMARY KEY,
    site_name VARCHAR(255) NOT NULL,
    location VARCHAR(255),
    description TEXT
);

CREATE TABLE itinerary_sites (
    itinerary_id UUID REFERENCES travel_itineraries(itinerary_id),
    site_id UUID REFERENCES cultural_sites(site_id),
    visit_date DATE,
    duration_hours INTEGER
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(100) UNIQUE,
    email VARCHAR(255) UNIQUE,
    password_hash VARCHAR(255) NOT NULL
);

