tables.table_schema:
CREATE TABLE participants (
   participant_id INT PRIMARY KEY,
   participant_name VARCHAR(50),
   nationality VARCHAR(50),
   age INT
);

CREATE TABLE ExchangePrograms (
    program_id SERIAL PRIMARY KEY,
    program_name VARCHAR(255) NOT NULL,
    program_type VARCHAR(50),
    program_location VARCHAR(100),
    program_date DATE
);

CREATE TABLE Participants (
    participant_id SERIAL PRIMARY KEY,
    participant_name VARCHAR(255) NOT NULL,
    participant_email VARCHAR(255) UNIQUE,
    participant_country VARCHAR(50)
);

CREATE TABLE Coordinators (
    coordinator_id SERIAL PRIMARY KEY,
    coordinator_name VARCHAR(255) NOT NULL,
    coordinator_email VARCHAR(255) UNIQUE,
    coordinator_department VARCHAR(100)
);

CREATE TABLE host_institutions (
  institution_id INT PRIMARY KEY,
  institution_name VARCHAR(255),
  country VARCHAR(50)
);
