tables.table_schema:
CREATE TABLE cultural_trends ( 
   trend_id INT PRIMARY KEY, 
   trend_name VARCHAR(255) NOT NULL, 
   trend_description TEXT, 
   trend_year INT 
 );

CREATE TABLE cultural_trends (
    trend_id INT PRIMARY KEY,
    trend_name VARCHAR(50) NOT NULL,
    trend_description TEXT,
    trend_influence_id INT REFERENCES trend_influences(influence_id)
);

CREATE TABLE trend_influences (
    influence_id INT PRIMARY KEY,
    influence_category VARCHAR(50) NOT NULL,
    influence_description TEXT
);

CREATE TABLE trend_data (
    data_id INT PRIMARY KEY,
    trend_id INT REFERENCES cultural_trends(trend_id),
    data_date DATE NOT NULL,
    data_value DECIMAL(10, 2) NOT NULL
);

CREATE TABLE trend_category (
    category_id SERIAL PRIMARY KEY,
    category_name VARCHAR(50) NOT NULL
);

CREATE TABLE trend_relation (
    trend_id INT REFERENCES cultural_trends(trend_id),
    influencer_id INT REFERENCES influencers(influencer_id),
    PRIMARY KEY (trend_id, influencer_id)
);

CREATE TABLE trend_category_relation (
    trend_id INT REFERENCES cultural_trends(trend_id),
    category_id INT REFERENCES trend_category(category_id),
    PRIMARY KEY (trend_id, category_id)
);
