tables.table_schema:
CREATE TABLE energy_consumption (
    hotel_id INT,
    room_number INT,
    date DATE,
    energy_usage DECIMAL
);

CREATE TABLE Hotels (
    hotel_id INT PRIMARY KEY,
    hotel_name VARCHAR(255),
    location VARCHAR(255),
    energy_consumption DECIMAL
);

CREATE TABLE hotel (
    hotel_id INT PRIMARY KEY,
    name VARCHAR(255),
    location VARCHAR(255),
    star_rating INT
);

CREATE TABLE room (
    room_id INT PRIMARY KEY,
    hotel_id INT,
    room_type VARCHAR(50),
    price DECIMAL(10, 2)
);

CREATE TABLE energy_consumption (
    reading_id INT PRIMARY KEY,
    room_id INT,
    reading_datetime TIMESTAMP,
    energy_usage DECIMAL(10, 2)
);
