tables.table_schema:
CREATE TABLE waste_management_practices (
  destination_id INT PRIMARY KEY,
  destination_name VARCHAR(255),
  recycling_efforts BOOLEAN
);

CREATE TABLE waste_management_practices(
  destination_id INT PRIMARY KEY,
  destination_name VARCHAR(50),
  recycling_level INT
);

CREATE TABLE waste_categories(
  category_id INT PRIMARY KEY,
  category_name VARCHAR(50),
  recycling_policy TEXT
);

CREATE TABLE WasteManagementPractices (
  location_id INT PRIMARY KEY,
  location_name VARCHAR(255),
  recycling_program VARCHAR(100),
  waste_collection_schedule VARCHAR(100)
);

CREATE TABLE waste_management_sites (
    site_id INT PRIMARY KEY,
    site_name VARCHAR(255),
    location VARCHAR(255),
    waste_type VARCHAR(50)
);

CREATE TABLE recycling_bins (
    bin_id INT PRIMARY KEY,
    bin_location VARCHAR(255),
    capacity INT,
    waste_accepted VARCHAR(50)
);

CREATE TABLE waste_collection_schedule (
    schedule_id INT PRIMARY KEY,
    site_id INT,
    day_of_week VARCHAR(15),
    collection_time TIME
);

CREATE TABLE recycling_statistics (
    stat_id INT PRIMARY KEY,
    site_id INT,
    year INT,
    recycling_rate DECIMAL(5,2)
);

CREATE TABLE waste_collection_team (
    team_id INT PRIMARY KEY,
    team_name VARCHAR(255),
    team_lead VARCHAR(50),
    phone_number VARCHAR(15)
);
