tables.table_schema:
CREATE TABLE accommodations (
    accommodation_id SERIAL PRIMARY KEY,
    accommodation_name VARCHAR(255) NOT NULL,
    water_usage DECIMAL
);

CREATE TABLE bookings (
    booking_id SERIAL PRIMARY KEY,
    accommodation_id INT REFERENCES accommodations(accommodation_id),
    guest_name VARCHAR(255) NOT NULL,
    check_in_date DATE,
    check_out_date DATE
);

CREATE TABLE conservation_strategies (
    strategy_id SERIAL PRIMARY KEY,
    strategy_name VARCHAR(255) NOT NULL,
    description TEXT
);

CREATE TABLE strategy_assignments (
    assignment_id SERIAL PRIMARY KEY,
    strategy_id INT REFERENCES conservation_strategies(strategy_id),
    accommodation_id INT REFERENCES accommodations(accommodation_id),
    start_date DATE,
    end_date DATE
);

CREATE TABLE water_monitoring (
    monitor_id SERIAL PRIMARY KEY,
    accommodation_id INT REFERENCES accommodations(accommodation_id),
    monitoring_date DATE,
    water_consumed DECIMAL
);
