tables.table_schema:
CREATE TABLE habitats (
  habitat_id INT PRIMARY KEY,
  habitat_name VARCHAR(50),
  habitat_description TEXT
);

CREATE TABLE wildlife_species (
  species_id INT PRIMARY KEY,
  species_name VARCHAR(50),
  species_category VARCHAR(50),
  habitat_id INT REFERENCES habitats(habitat_id)
);

CREATE TABLE habitats (
  habitat_id SERIAL PRIMARY KEY,
  habitat_name VARCHAR(50) NOT NULL,
  habitat_type VARCHAR(50),
  region VARCHAR(50) NOT NULL
);

CREATE TABLE animals (
  animal_id SERIAL PRIMARY KEY,
  animal_name VARCHAR(50) NOT NULL,
  species VARCHAR(50) NOT NULL,
  habitat_id INT REFERENCES habitats(habitat_id)
);

CREATE TABLE tourism_sites (
  site_id SERIAL PRIMARY KEY,
  site_name VARCHAR(100) NOT NULL,
  location VARCHAR(100) NOT NULL,
  habitat_id INT REFERENCES habitats(habitat_id)
);

CREATE TABLE Habitats (
    habitat_id INT PRIMARY KEY,
    habitat_name TEXT,
    location TEXT,
    habitat_type TEXT
);

CREATE TABLE Tourists (
    tourist_id INT PRIMARY KEY,
    tourist_name TEXT,
    age INT,
    nationality TEXT
);

CREATE TABLE habitat (
  id SERIAL PRIMARY KEY,
  name VARCHAR(100) NOT NULL,
  location VARCHAR(100) NOT NULL,
  habitat_type VARCHAR(50) NOT NULL
);

CREATE TABLE animal (
  id SERIAL PRIMARY KEY,
  name VARCHAR(100) NOT NULL,
  species VARCHAR(100) NOT NULL,
  habitat_id INT REFERENCES habitat(id)
);
