tables.table_schema:
CREATE TABLE solo_travel_safety_tips (
	tip_id INT PRIMARY KEY,
	tip_description TEXT,
	precautions TEXT
);

CREATE TABLE travel_safety_tips (
    tip_id INT PRIMARY KEY,
    tip_description TEXT,
    safety_level INT
);

CREATE TABLE travel_precautions (
    precaution_id INT PRIMARY KEY,
    precaution_description TEXT,
    precaution_location TEXT
);

CREATE TABLE solo_travel_safety_tips (
    tip_id INT PRIMARY KEY,
    tip_title TEXT NOT NULL,
    tip_description TEXT
);

CREATE TABLE travel_precautions (
    precaution_id INT PRIMARY KEY,
    precaution_title TEXT NOT NULL,
    precaution_details TEXT
);

CREATE TABLE travel_destination (
    destination_id INT PRIMARY KEY,
    destination_name TEXT NOT NULL,
    safety_level TEXT
);

CREATE TABLE SoloTravelTips (
    id INT PRIMARY KEY,
    tip_title TEXT,
    tip_description TEXT,
    category TEXT
);

CREATE TABLE TravelChecklist (
    id INT PRIMARY KEY,
    item_name TEXT,
    description TEXT,
    category TEXT
);

CREATE TABLE TravelWarnings (
    id INT PRIMARY KEY,
    country_name TEXT,
    warning_description TEXT
);
