tables.table_schema:
CREATE TABLE itinerary (
  id INT PRIMARY KEY,
  destination VARCHAR,
  start_date DATE,
  end_date DATE
);

CREATE TABLE bookings (
  booking_id INT PRIMARY KEY,
  itinerary_id INT,
  booking_date DATE
);

CREATE TABLE solo_travelers (
  traveler_id INT PRIMARY KEY,
  first_name VARCHAR,
  last_name VARCHAR,
  email VARCHAR
);

CREATE TABLE payments (
  payment_id INT PRIMARY KEY,
  booking_id INT,
  amount DECIMAL,
  payment_date DATE
);
