tables.table_schema:
CREATE TABLE recommendations (
  id INT PRIMARY KEY,
  location VARCHAR(255),
  accommodation VARCHAR(255),
  activity VARCHAR(255)
);

CREATE TABLE solo_bnb_recommendations (
    recommendation_id INT PRIMARY KEY,
    accommodation_name VARCHAR(255),
    location VARCHAR(255),
    rating DECIMAL(3,2)
);

CREATE TABLE solo_travel_activities (
    activity_id INT PRIMARY KEY,
    activity_name VARCHAR(255),
    category VARCHAR(50),
    duration_hours INT
);

CREATE TABLE accommodation_recommendations (
    accommodation_id INT PRIMARY KEY,
    accommodation_name VARCHAR(255),
    location VARCHAR(255),
    rating FLOAT
);

CREATE TABLE traveler_details (
    traveler_id INT PRIMARY KEY,
    traveler_name VARCHAR(255),
    country VARCHAR(50),
    age INT
);

CREATE TABLE activities (
    id SERIAL PRIMARY KEY,
    name VARCHAR(255),
    category VARCHAR(50),
    description TEXT
);

CREATE TABLE users (
    id SERIAL PRIMARY KEY,
    username VARCHAR(50) UNIQUE,
    email VARCHAR(255) UNIQUE,
    password VARCHAR(255)
);
