tables.table_schema:
CREATE TABLE packing_lists (
    id SERIAL PRIMARY KEY,
    item_name VARCHAR(100) NOT NULL,
    quantity INT NOT NULL,
    trip_id INT REFERENCES trips(id) ON DELETE CASCADE
);

CREATE TABLE packing_lists (
    id INT PRIMARY KEY,
    destination VARCHAR(255),
    departure_date DATE,
    return_date DATE
);

CREATE TABLE solo_travelers (
    id INT PRIMARY KEY,
    traveler_name VARCHAR(255),
    packing_list_id INT REFERENCES packing_lists(id),
    checklist_id INT REFERENCES checklist(id)
);

CREATE TABLE packing_lists (
    id INT PRIMARY KEY,
    traveler_name VARCHAR(50),
    destination VARCHAR(100),
    packing_items TEXT
);

CREATE TABLE checklists (
    id INT PRIMARY KEY,
    traveler_name VARCHAR(50),
    trip_date DATE,
    checklist_items TEXT
);

CREATE TABLE trips (
    id INT PRIMARY KEY,
    traveler_name VARCHAR(50),
    destination VARCHAR(100),
    trip_date DATE
);

CREATE TABLE packing_lists (
  id INT PRIMARY KEY,
  item_name VARCHAR(255) NOT NULL,
  quantity INT,
  category VARCHAR(50)
);

CREATE TABLE trip_checklists (
  id INT PRIMARY KEY,
  item_name VARCHAR(255) NOT NULL,
  due_date DATE,
  completed BOOLEAN
);

CREATE TABLE packing_categories (
  id INT PRIMARY KEY,
  category_name VARCHAR(50) NOT NULL
);

CREATE TABLE trip_notes (
  id INT PRIMARY KEY,
  note_text TEXT,
  trip_id INT REFERENCES travel_destinations(id)
);
