tables.table_schema:
CREATE TABLE solo_travel_destinations (
  destination_id INT PRIMARY KEY,
  destination_name TEXT,
  avg_rating FLOAT,
  num_reviews INT
);

CREATE TABLE user_preferences (
  user_id INT PRIMARY KEY,
  preference_1 TEXT,
  preference_2 TEXT,
  preference_3 TEXT
);

CREATE TABLE destinations (
    destination_id INT PRIMARY KEY,
    destination_name VARCHAR(255) NOT NULL,
    rating FLOAT,
    user_reviews INT
);

CREATE TABLE preferences (
    preference_id INT PRIMARY KEY,
    user_id INT,
    destination_id INT,
    FOREIGN KEY (destination_id) REFERENCES destinations(destination_id)
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(255) NOT NULL,
    age INT,
    gender VARCHAR(10)
);

CREATE TABLE destinations (
    id INT PRIMARY KEY,
    name VARCHAR(100),
    description TEXT,
    rating FLOAT
);

CREATE TABLE preferences (
    id INT PRIMARY KEY,
    user_id INT,
    destination_id INT,
    category VARCHAR(50)
);

CREATE TABLE categories (
    id INT PRIMARY KEY,
    name VARCHAR(50)
);
