tables.table_schema:
CREATE TABLE solo_travelers ( 
    id SERIAL PRIMARY KEY, 
    name VARCHAR(50) NOT NULL, 
    age INT, 
    interests TEXT 
);

CREATE TABLE participant_status ( 
    trip_id INT REFERENCES trips(trip_id), 
    participant_id INT REFERENCES solo_travelers(id), 
    is_confirmed BOOLEAN DEFAULT FALSE, 
    PRIMARY KEY (trip_id, participant_id) 
);

CREATE TABLE SoloTravelers (
    traveler_id INT PRIMARY KEY,
    traveler_name VARCHAR(50),
    traveler_email VARCHAR(50),
    traveler_country VARCHAR(50)
);

CREATE TABLE Trips (
    trip_id INT PRIMARY KEY,
    trip_name VARCHAR(50),
    trip_destination VARCHAR(50),
    trip_date DATE
);

CREATE TABLE Connections (
    connection_id INT PRIMARY KEY,
    traveler_id INT,
    connection_date DATE
);

CREATE TABLE GroupTrips (
    group_trip_id INT PRIMARY KEY,
    trip_id INT,
    connection_id INT
);

CREATE TABLE Messages (
    message_id INT PRIMARY KEY,
    sender_id INT,
    receiver_id INT,
    message_text TEXT
);
