tables.table_schema:
CREATE TABLE budget_limits (
  id INT PRIMARY KEY,
  department VARCHAR(50),
  budget_limit DECIMAL,
  year INT
);

CREATE TABLE budget_limits (
	limit_id INT PRIMARY KEY,
	category VARCHAR(50),
	monthly_limit DECIMAL(10,2)
);

CREATE TABLE cost_saving_opportunities (
	opportunity_id INT PRIMARY KEY,
	category VARCHAR(50),
	opportunity_description TEXT
);

CREATE TABLE travel_reports (
    report_id INT PRIMARY KEY,
    employee_id INT,
    report_date DATE,
    total_expenses DECIMAL
);

CREATE TABLE employees (
    employee_id INT PRIMARY KEY,
    employee_name VARCHAR(50),
    department VARCHAR(50)
);

CREATE TABLE expenses (
    expense_id INT PRIMARY KEY,
    report_id INT,
    expense_date DATE,
    expense_amount DECIMAL
);

CREATE TABLE budgets (
    department VARCHAR(50) PRIMARY KEY,
    budget_limit DECIMAL
);

CREATE TABLE travel_budget (
  id SERIAL PRIMARY KEY,
  budget_type VARCHAR(50) NOT NULL,
  budget_limit DECIMAL NOT NULL
);

CREATE TABLE expenditure_reports (
  id SERIAL PRIMARY KEY,
  report_date DATE NOT NULL,
  total_expenditure DECIMAL NOT NULL,
  budget_type VARCHAR(50) NOT NULL
);

CREATE TABLE expense_categories (
  id SERIAL PRIMARY KEY,
  category_name VARCHAR(50) NOT NULL
);
