tables.table_schema:
CREATE TABLE travel_expenses (
    id INT PRIMARY KEY,
    date DATE,
    expense_type VARCHAR(50),
    amount DECIMAL(10, 2)
);

CREATE TABLE budget_plan (
    id INT PRIMARY KEY,
    month VARCHAR(20),
    total_budget DECIMAL(10, 2),
    actual_spending DECIMAL(10, 2)
);

CREATE TABLE TravelExpenses ( 
    expense_id INT PRIMARY KEY, 
    date DATE, 
    category VARCHAR(50), 
    amount DECIMAL(10, 2) 
);

CREATE TABLE BudgetForecast ( 
    forecast_id INT PRIMARY KEY, 
    forecast_date DATE, 
    budget DECIMAL(10, 2) 
);

CREATE TABLE TravelDestinations ( 
    destination_id INT PRIMARY KEY, 
    destination_name VARCHAR(100), 
    country VARCHAR(50) 
);

CREATE TABLE TravelExpenses (
    id INT PRIMARY KEY,
    destination VARCHAR,
    travel_date DATE,
    expenses DECIMAL
);

CREATE TABLE BudgetPlans (
    id INT PRIMARY KEY,
    year INT,
    total_budget DECIMAL
);
